import './App.css';
import { useDispatch } from 'react-redux'; //llama a las funciones para alterar el estado
import { useSelector } from 'react-redux'; //aclara como va a traer los datos desde el estado
import TaskList from './components/TaskList';
import TaskForm from './components/TaskForm';

function App() {
  const taskState = useSelector(state => state.task); //Esta linea junto con el console.log actúan parecido a un useState, pero a diferencia de
  console.log(taskState);                             //que si creo otro componente, voy a poder controlarlo.

  return (
    <div className="App">
      <h1>Hello world</h1>

      <TaskForm />
      <TaskList />
    </div>
  );
}

export default App;
