import { createSlice } from "@reduxjs/toolkit";

const initialState = [
    {
        id: "1",
        title: "Task1",
        description: "Task 1 description",
        completed: false
    },
    {
        id: "2",
        title: "Task2",
        description: "Task 2 description",
        completed: false
    }

];

//actúa parecido a useState
export const taskSlice = createSlice({
    name: 'task',
    initialState, //puede ser cualquier valor inicial
    reducers: { //funciones para alterar el estado
        addTask: (state, action) => {
            state.push(action.payload) //añade la nueva tarea
        },
        deleteTask: (state, action) => {//elimina la tarea
            state.find(task => task.id === action.payload.id)//por cada tarea que estés comparando cuyo id sea igual que el payload.id, elimínalo.

        }
    }
})

export const {addTask, deleteTask} = taskSlice.actions;
export default taskSlice.reducer;
